import { useState, useEffect } from 'react'

function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data')
      const data = await fetch('https://covid19.mathdro.id/api')
        .then(res => res.json())
      setStats(data);
    }
    fetchData();
  }, [])
}

const Stats = () => {
  const stats = useStats();
  console.log(stats)
  return (
    <div>
      <p>Stats:</p>
    </div>
  )
}


const Page = () => {
  return <div>
    <Stats></Stats>
  </div>
}

export default Page;
